                data.btn.map(item=> <button onClick={()=>{this.tapNum(item.val)}}
                 className={css.knop}>{item.val}</button>)
                 }
                 {
                data.opr.map(item=> <button onClick={()=>{this.tapOpr(item.val)}}
                 className={css.knop}>{item.val}</button>)
                 }
            </div>

        </div>
    )
}
