import React from "react";
import css from './Calc.module.css'

let data = {
    btn:[
       {val:'7'},
       {val:'8'},
       {val:'9'},
       {val:'/',bg: 'orange'},
       {val:'4'},
       {val:'5'},
       {val:'6'},
       {val:'-',bg: 'orange'},
       {val:'1'},
       {val:'2'},
       {val:'3'},
       {val:'+',bg: 'orange'},
       {val:'0'},
       {val:','},
       {val:'%'},

    ],
    opr:[
        {val:'C',bg: 'orange'},
        {val:'X'},
        {val:'='},
    ]
}

export class Calc extends React.Component{
    constructor(){
        super()
        this.state = {
            btnVal: '0',

        }
        this.innerInp = React.createRef()

    }

    tapNum(value) {
        let inpVal = value;
        let inner = this.innerInp.current;

        this.setState({ 
            btnVal: inpVal });

        switch (value) {
            case '0':
                if (inner.value === '0') {
                    inner.value = '';

                }
                break;
            default:
                if (inner.value === '0') {
                    inner.value = '';
                }
                inner.value += value;
                this.setState({ btnVal: inner.value });
                break;
        };
    }

tapOpr(value){
    let inner = this.innerInp.current
    switch(value){
        case 'X':
        inner.value =( inner.value.length ===1) ? '0': inner.value.substring(0, inner.value.length -1)
        break;
       case 'C':
        inner.value ='0';
        break;
        case '=':
            try {
                inner.value = eval(inner.value);
            } catch (error) {
                inner.value = 'Ошибка!!';
                setTimeout(() => {
                    inner.value = '0'
                }, 1500);
            }
            break;
        default:
            break;
    };
    };




    render(){
        return <div className={css.container}>
            <div className={css.main}>
                <input className={css.input} type="number" ref={this.innerInp} value={this.state.btnVal} />
<div className={css.btn}>
{data.btn.map((item)=>{
    return <button onClick={()=>{this.tapNum(item.val,)}} style={{backgroundColor: item.bg}} className={css.knop}>{item.val}</button>
})}
{data.opr.map((item)=>{
    return <button onClick={()=>{this.tapOpr(item.val)}}  style={{backgroundColor: item.bg}} className={css.knop}>{item.val}</button>
})}
</div>


</div>
        </div>
    }
}
