import React from "react";
import css from './Calc.module.css'

let data = {
    btn:[
       {val:'1'},
       {val:'2'},
       {val:'3'},
       {val:'4'},
       {val:'5'},
       {val:'6'},
       {val:'7'},
       {val:'8'},
       {val:'8'},
       {val:'0'},
       {val:'-'},
       {val:'+'},
       {val:'*'},
       {val:'/'},
       {val:'%'},

    ],
    opr:[
        {val:'C'},
        {val:'X'},
        {val:'='},
    ]
}

export class Calc extends React.Component{
    constructor(){
        super()
        this.state = {
            btnVal: '0',

        }
        this.innerInp = React.createRef()

    }

tapNum(value){
    let inpVal = value
    let inner = this.innerInp.current

    this.setState({btnVal: inpVal})
    if(inner.value === '0'){
        inner.value =''}

      inner.value += inpVal
      this.setState({btnVal: inner.value})

}
tapOpr(value){
    let inner = this.innerInp.current

    if(value==='X'){
        (inner.value.length === 1) ? inner.value ='0' : inner.value = inner.value.substring(0, inner.value.length -1)
        }
        else if(value === 'C'){
          inner.value = '0'
        }
        else if(value === '='){
            inner.valueeval(inner.value)
        }



}


    render(){
        return <div className={css.container}>
            <div className={css.main}>
                <input type="nuber" ref={this.innerInp} value={this.state.btnVal} />
<div className={css.btn}>
{data.btn.map((item)=>{
    return <button onClick={()=>{this.tapNum(item.val)}} className={css.knop}>{item.val}</button>
})}
{data.opr.map((item)=>{
    return <button onClick={()=>{this.tapOpr(item.val)}} className={css.knop}>{item.val}</button>
})}
</div>


</div>
        </div>
    }
}
